交换内容，形参影响实参
#include &lt;stdio.h&gt;
void swap(int *p1,int *p2)
{
    int temp;
    temp = *p1;
    *p1 = *p2;
    *p2 = temp;
}
int main()
{
    int a,b;
    int *p1 = &amp;a,*p2 = &amp;b;
    scanf(&quot;%d%d&quot;,p1,p1);
    swap(p1,p2);
    printf(&quot;%d,%d&quot;,*p1,*p2);
}

交换地址，形参不影响实参
#include &lt;stdio.h&gt;
void swap(int *p1,int *p2)
{
    int *temp;
    temp = p1;
    p1 = p2;
    p2 = temp;
}
int main()
{
    int a,b;
    int *p1 = &amp;a,*p2 = &amp;b;
    scanf(&quot;%d%d&quot;,p1,p1);
    swap(p1,p2);
    printf(&quot;%d,%d&quot;,*p1,*p2);
}

出错，temp指针所指地址未初始化，导致出错
#include &lt;stdio.h&gt;
void swap(int *p1,int *p2)
{
    int *temp;
    *temp = *p1;
    *p1 = *p2;
    *p2 = *temp;
}
int main()
{
    int a,b;
    int *p1 = &amp;a,*p2 = &amp;b;
    scanf(&quot;%d%d&quot;,p1,p1);
    swap(p1,p2);
    printf(&quot;%d,%d&quot;,*p1,*p2);
}
